import React, { useState } from 'react';
import { likeArticle } from '../services/api';

function ArticleCard({ article, onLike }) {
  const [localLikes, setLocalLikes] = useState(article.likes);

  // Check if this is a default article
  const isDefaultArticle = article._id.startsWith('default');

  const handleLike = async () => {
    if (isDefaultArticle) {
      return;  // Don't allow liking default articles
    }

    try {
      await likeArticle(article._id);
      setLocalLikes(localLikes + 1);  // Update the likes count in the UI immediately
      onLike(article._id);  // Call parent function to handle other updates if needed
    } catch (error) {
      console.error('Error liking article', error);
    }
  };

  return (
    <div>
      <h3>{article.title}</h3>
      <p>{article.description}</p>
      <p>Likes: {localLikes}</p>  {/* Display the updated like count */}
      {!isDefaultArticle && <button onClick={handleLike}>Like</button>}  {/* Disable like button for default articles */}
    </div>
  );
}

export default ArticleCard;
