function ArticleCard({ article, onLike }) {
  const [localLikes, setLocalLikes] = useState(article.likes);

  const handleLike = async () => {
    try {
      await likeArticle(article._id);  // Call the API to like the article
      setLocalLikes(localLikes + 1);   // Update local likes count
      onLike();  // Trigger parent component's fetchArticlesList function
    } catch (error) {
      console.error('Error liking article', error);
    }
  };

  return (
    <div>
      <h3>{article.title}</h3>
      <p>{article.description}</p>
      <p>Likes: {localLikes}</p>
      <button onClick={handleLike}>Like</button>
    </div>
  );
}

export default ArticleCard;
