import React, { useState } from 'react';
import { FaThumbsUp } from 'react-icons/fa';  // Thumbs-up icon
import Modal from './Modal';  // Import the Modal component

function ArticleCard({ article, onLike }) {
  const [isModalOpen, setIsModalOpen] = useState(false);

  const handleLike = async () => {
    try {
      await onLike(article._id);
    } catch (error) {
      console.error('Error liking article', error);
    }
  };

  const toggleModal = () => {
    setIsModalOpen(!isModalOpen);
  };

  return (
    <div className="article-card">
      <h3>{article.title}</h3>
      {article.imageUrl && <img src={article.imageUrl} alt={article.title} onClick={toggleModal} />}
      <p>{article.description}</p>
      <button onClick={handleLike}>
        <FaThumbsUp /> {article.likes}
      </button>
      <button className="edit-article-button">Edit Article</button>

      {/* Modal to display full article */}
      <Modal isOpen={isModalOpen} onClose={toggleModal}>
        <h2>{article.title}</h2>
        {article.imageUrl && <img src={article.imageUrl} alt={article.title} />}
        <p>{article.text}</p>  {/* Full article text */}
      </Modal>
    </div>
  );
}

export default ArticleCard;
