import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { fetchArticleById, updateArticle } from '../services/api';

function EditArticlePage() {
  const { id } = useParams();  // Get article ID from URL
  const navigate = useNavigate();

  const [article, setArticle] = useState({
    title: '',
    description: '',
    text: '',
    imageUrl: ''
  });

  useEffect(() => {
    const loadArticle = async () => {
      try {
        const response = await fetchArticleById(id);
        setArticle(response.data);
      } catch (error) {
        console.error('Error fetching article:', error);
      }
    };
    loadArticle();
  }, [id]);

  const handleChange = (e) => {
    setArticle({ ...article, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await updateArticle(id, article);
      navigate('/articles');  // Navigate back to articles after successful update
    } catch (error) {
      console.error('Error updating article:', error);
    }
  };

  return (
    <div className="page-container">
      <h2>Edit Article</h2>
      <form onSubmit={handleSubmit}>
        <label>Title:</label>
        <input
          type="text"
          name="title"
          value={article.title}
          onChange={handleChange}
          required
        />

        <label>Description:</label>
        <input
          type="text"
          name="description"
          value={article.description}
          onChange={handleChange}
          required
        />

        <label>Text:</label>
        <textarea
          name="text"
          value={article.text}
          onChange={handleChange}
          required
        />

        <label>Image URL:</label>
        <input
          type="text"
          name="imageUrl"
          value={article.imageUrl}
          onChange={handleChange}
        />

        <button type="submit" className="submit-button">Update Article</button>
      </form>
    </div>
  );
}

export default EditArticlePage;
